private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
try
{
    Class.forName("java.sql.DriverManager");
    Connection con = (Connection)
         DriverManager.getConnection("jdbc:mysql://localhost:3306/employee","root","");
     Statement stmt = null;
     ResultSet rs = null;
     stmt = con.createStatement();
        String EmpID = t.getText();
        String Name = a1.getText();
        String Desig  = a2.getText();
        String Date = a3.getText();
        String Dept = a4.getText();
        String Sal  = a5.getText();
        String Commn = a6.getText();
        String Age  = a7.getText();
        
    String query = "Update emp set name='"+(Name)+"',job='"+(Desig)+"',date='"+(Date)+"',dept='"+(Dept)+"',sal='"+(Sal)+"',comm='"+(Commn)+"',age='"+(Age)+"'where empno='"+(EmpID)+"';";
   
    int rowsEffected = stmt.executeUpdate(query);
    if(rowsEffected==0)
        JOptionPane.showMessageDialog(this, "Please Try Again");
    else
        JOptionPane.showMessageDialog(this, "Record Updated");
}catch(Exception e)
{
     JOptionPane.showMessageDialog(this, e.getMessage());
}    
}//GEN-LAST:event_jButton5ActionPerformed

