private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
Double val = Double.parseDouble(t.getText());
try
{
 Class.forName("java.sql.DriverManager");
 Connection con = (Connection)
 DriverManager.getConnection("jdbc:mysql://localhost:3306/employee","root","");
 Statement stmt = (Statement) con.createStatement();
 String query = "Select * from emp where empno='"+val+"';";
 ResultSet rs =  stmt.executeQuery(query);
 if(rs.next())
    {   String id = rs.getString("empno");
        String Name = rs.getString("name");
        String job = rs.getString("job");
        String date = rs.getString("date");
        String dept = rs.getString("dept");
        String sal = rs.getString("sal");
        String comm = rs.getString("comm");
        String age = rs.getString("age");
        
        t1.setText(id);
        a1.setText(Name);
        a2.setText(job);
        a3.setText(date);
        a4.setText(dept);
        a5.setText(sal);
        a6.setText(comm);
        a7.setText(age);
        
        JOptionPane.showMessageDialog(this,"One Record Found ");} 
        else   
        JOptionPane.showMessageDialog(this,"No Record Found ");}
    catch(Exception e)
    {JOptionPane.showMessageDialog(this, e.getMessage());}
}//GEN-LAST:event_jButton4ActionPerformed

